$this->MultiCell(200, 5,"".$data['pregunta']."\n", 0, 'L', 1, 0, $span, $aux, true);								//echo "</td></tr><tr><td colspan='2'>".$data['pregunta']."</td></tr><tr><td>";			$campoval=array_filter(explode('<div class="respuestas" >',$data['campos']));			$campovalres=array_filter(explode('<div class="respuestas" >',$data['respuestas']));			$aux=$this->addli($aux,$tam1);			$cont=1;			$acumuladorcampos="";			$tamacu=0;			foreach ($campoval as $campo) {				$campo=str_replace("</div>", "", $campo);				$campovalres[$cont]=str_replace("</div>", "", $campovalres[$cont]);				$acumuladorcampos.=" ".$campo.": ".$campovalres[$cont];								$tamacu+=strlen($campo.": ".$campovalres[$cont]);				$cont++;			}			$this->MultiCell(200, 10,$acumuladorcampos, 0, 'L', 1, 0, $span, $aux, true);							if(strlen($acumuladorcampos)>=30){				   $aux=$this->addli($aux,$tam1);				   //$this->MultiCell(90, 10,$aux, 0, 'J', 1, 0, $span, $aux, true);				}			if(strlen($acumuladorcampos)>=60){				   $aux=$this->addli($aux,$tam1);				   //$this->MultiCell(90, 10,$aux, 0, 'J', 1, 0, $span, $aux, true);				}				if(strlen($acumuladorcampos)>=90){				   $aux=$this->addli($aux,$tam1);				   //$this->MultiCell(90, 10,$aux, 0, 'J', 1, 0, $span, $aux, true);				}						}else if($data["tipo"]=="radio"){			$cont=1;			$campovalres=array_filter(explode('<div class="respuestas" >',$data['respuestas']));			$campovalres[$cont]=str_replace("</div>", "", $campovalres[$cont]);			$campovalres[$cont]=str_replace("</div>", "", $campovalres[$cont]);			$this->MultiCell(200, 5,$data['pregunta'].": ".$campovalres[$cont], 0, 'L', 1, 0, $span, $aux, true);				$cont++;			$aux=$this->addli($aux,$tam1);		}else if($data["tipo"]=="select"){			$cont=1;			$campovalres=array_filter(explode('<div class="respuestas" >',$data['respuestas']));			$campovalres[$cont]=str_replace("</div>", "", $campovalres[$cont]);			$campovalres[$cont]=str_replace("</div>", "", $campovalres[$cont]);			$this->MultiCell(90, 5,$data['pregunta'].": ".$campovalres[$cont], 0, 'L', 1, 0, $span, $aux, true);				$cont++;			$aux=$this->addli($aux,$tam1);		}else if($data["tipo"]=="checkbox"){			$this->MultiCell(200, 5,"".$data['pregunta']."\n", 0, 'J', 1, 0, $span, $aux, true);				//$campoval=array_filter(explode('<div class="respuestas" >',$data['campos']));			$campovalres=array_filter(explode('<div class="respuestas" >',$data['respuestas']));			$cont=1;			$acumuladorcampos="";			foreach ($campovalres as $campo) {				$campo=str_replace("</div>", "", $campo);				$campovalres[$cont]=str_replace("</div>", "", $campovalres[$cont]);				$acumuladorcampos.=$campovalres[$cont].", ";				$cont++;			}			$this->MultiCell(200, 10,$acumuladorcampos, 0, 'L', 1, 0, $span, $aux, true);				$aux=$this->addli($aux,$tam1);            $cont++;	          	if(strlen($acumuladorcampos)>=90){				   $aux=$this->addli($aux,$tam1);				   //$this->MultiCell(90, 10,$aux, 0, 'J', 1, 0, $span, $aux, true);				}		} 		    						}				}		function addli($li,$aumento,$head=0){			if($li>300){				$this->addpage();				$li=35;			}						$li=$li+$aumento;			return $li;		}	  function cabecera($datos,$pos, $codigofact){				         $this->SetFillColor(255, 255, 255);		 $image_file = "cabecera1.jpg";                      $this->Image($image_file,5, $pos, 75, '', 'JPG', '', 'T', false, 100, '', false, false, 0, false, false, false);    	$this->SetFont('helvetica', 'B', 7);				   $texto1=$this->remplazarcaracteres(" Licencia de Salud Ocupacional Reseolucion No. 0668 07/03/2007 \nCodigo de prestador de servicio de salud No 700010120303\nMinproteccionsocial Resol. No 0197 del 18 de febrero de 2010\nVigilidado Supersalud ");			$this->MultiCell(120, 20,$texto1, 0, 'C', 1, 0, 105, $pos, true);			$pos=$pos+14;    					 $image_file = "../fotos/pacientes/".$datos[0]['foto'];                      $this->Image($image_file,170, $pos+25, 30, '', 'JPG', '', 'T', false, 