aux>$mayoraux)							{							  $mayoraux=$aux;							}													$aux=$li;						$enx=$enx+50;						$entro=0;						}								}																}			if( $cambiodelinea==4)					{					$tam=  $mayoraux- $li;					//$this->RoundedRect(4, $li, 200,$tam , 3.50, '0000',null,$style6);				   // $this->Line(51, $li, 51,$mayoraux);					////$this->Line(101, $li, 101,$mayoraux);					//$this->Line(151, $li, 151,$mayoraux);						$aux=$mayoraux;						$li=$mayoraux;						$enx=5;						$entro=0;					}	 }				if( $cambiodelinea!=0)     {					$tam=  $mayoraux- $li;					//$this->RoundedRect(4, $li, 200,$tam , 3.50, '0000',null,$style6);					// $this->Line(51, $li, 51,$mayoraux);					//$this->Line(101, $li, 101,$mayoraux);					//$this->Line(151, $li, 151,$mayoraux);					$li=$mayoraux+4;					      	  		}				$this->SetFont('helvetica', '', 7);		return $li;		}function examen_fisico($datos,$examen_fisico,$li,$tam1){		$this->SetFont('helvetica', 'B', 10);		 $li=$li+$tam1;		 $this->MultiCell(200, 5,"EXAMEN FÍSICO\n", 0, 'L', 1, 0, 5, $li, true);		 $li=$li+$tam1;		 $texto="";		 $this->SetFont('helvetica', '', 8);		 $cont=0;		 $aumento=1;		if($examen_fisico!="")//si no esta vacio			foreach ($examen_fisico as  $datosac) {								if($cont==1){					$aumento=20;				}else if($cont==2){					$cont=0;						$aumento=1;					$li=$li+4;				}else if($cont==3){									}else {									}				$texto=" <b> ".ucfirst(strtolower($datosac['nombre'])).":</b>";				$texto.=" ".ucfirst(strtolower($datosac['observacion']));				$this->MultiCell(150, 5,$texto, 0, 'J', 1, 0, $aumento*5, $li, true,0,true);							$cont++;									}						$li=$li+$tam1;							return $li;}function examenfisico_imc($datos,$examen_fisicoimc,$li,$tam1){		$this->SetFont('helvetica', 'B', 10);		 $texto="";		 $this->SetFont('helvetica', '', 8);		 $cont=0;		 $aumento=1;		 $li=$li+4;		 $textoimc="";		if($examen_fisicoimc!="")//si no esta vacio			foreach ($examen