mendaciones="";while ($filaref= $bd->obtener_fila($resultref,0)){	$datosdbrecomendaciones[]=array(						'recomendacion'=>$filaref['recomendacion'],						'codigo'=>$filaref['codigo'],						'rgeneral'=>$filaref['rgeneral']							);}if($datosdb!=""){	$pdf->tabladedatos(			$datosdb,			$examen_fisico,			$habitos,			$informacion_o,			$factores,			$revision,			$antecedentes,			$datosdbactitud,			$datosdbrecomendaciones,			$diagnosticos,			$examen_fisicoimc,			$codigofact);}else{	echo "No hay datos para esta historia $codigofact";	exit;}// set some text to print// print a block of text using Write()$pdf->Write($h=0, "", $link='', $fill=0, $align='C', $ln=true, $stretch=0, $firstline=false, $firstblock=false, $maxh=0);// ---------------------------------------------------------//Close and output PDF document$pdf->Output('formatos.pdf', 'I');//============================================================+// END OF FILE//============================================================+?>